class image extends StatefulWidget {
  const image({super.key});

  @override
  State<image> createState() => _imageState();
}

class _imageState extends State<image> {
  Widget buildImage(String urlImage, int index) => Container(
        color: Color.fromARGB(255, 127, 193, 255),
        child: Image(image: AssetImage(urlImage)),
      );
  int currentPos = 0;


  ////////////////รูปสไลด์ เพิ่มหรือลบในลิสต์นี้ได้เลยครับ
  final urlImages = [
    'lib/images/med1.png',
  ];
  @override
  Widget build(BuildContext context) {
    return Column(
      children: [
        CarouselSlider.builder(
          itemCount: urlImages.length,
          itemBuilder: ((context, index, realIndex) {
            final urlImage = urlImages[index];
            return buildImage(urlImage, index);
          }),
          options: CarouselOptions(
            // onPageChanged: ,
            onPageChanged: (index, reason) {
              setState(() {
                currentPos = index;
              });
            },

            autoPlay: false,
            // enlargeCenterPage: true,
            height: 250,
            viewportFraction: 1,
          ),
        ),
        Row(
          mainAxisAlignment: MainAxisAlignment.center,
          children: urlImages.map((url) {
            int index = urlImages.indexOf(url);
            return Container(
              margin: const EdgeInsets.all(5),
              width: 25.0,
              height: 10.0,
              decoration: BoxDecoration(
                shape: BoxShape.circle,
                color: currentPos == index
                    ? Color.fromRGBO(0, 94, 255, 0.878)
                    : Color.fromRGBO(98, 118, 170, 0.4),
              ),
            );
          }).toList(),
        ),
      ],
    );
  }
}

